def Find2MACIndex(MACAddr,ListToFind):
    MACIndex=-1
    MACLoc=str(ListToFind).find(str(MACAddr))
    if MACLoc!=-1:
        MACIndex=int(MACLoc) -2
        MACIndex=MACIndex/42
        if ListToFind[MACIndex]!=MACAddr:
            MACIndex=-1
    return MACIndex

