def RearrangeReturn(sLine):
    x=0
    tmplist=[]
    while x<len(sLine):
        tline=sLine[x]
        tl=int(len(tline))
        if tl>0:
            nx=tline.rfind("\\r",0,tl)
            if nx>0:
                nx=nx+2
                nline=tline[nx:]
                tmplist.append (str(nline))
        x += 1
    return tmplist

