def CheckWPSLog(WBSSID):
    line1="";line2="";line3=""
    l=1
    ReaverLog=ReaverPath + str(WBSSID) + ".wpc"
    if IsFileDirExist(ReaverLog)=="F":
        with open(ReaverLog,"r") as f:
            for line in f:
                if l==1:
                    line1=str(line).replace("\n","")
                if l==2:
                    line2=str(line).replace("\n","")
                if l==3:
                    line3=str(line).replace("\n","")
                l += 1
                if l>3:
                    f.close()
                    return str(line1) + ", " + str(line2) + ", " + str(line3)
                    continue
    return ""

